Meteor.methods({ 
  'insertTweet': function(tweet) {

  	twiit = _.extend(tweet, {
      user: Meteor.user().username,
      message: tweet,
      timestamp: new Date()
    });
  	/*
    if (Meteor.user()) {
      Twitts.insert({
        message: tweet,
        user: Meteor.user().username,
        timestamp: new Date()
      });
    }
	*/
	twiit._id = Twitts.insert(twiit);
	createTwiitNotification(twiit);
  }
});
