Meteor.methods({ 
  'insertTweet': function(tweet) {
  	/*
  	twiit = _.extend(tweet, {
      user: Meteor.user().username,
      message: tweet,
      timestamp: new Date()
    });
    */
    if (Meteor.user()) {
      twiit._id = Twitts.insert({
        message: tweet,
        user: Meteor.user().username,
        timestamp: new Date()
      });
    }
	createTwiitNotification(twiit._id);
  }
});
