Template.twiitPage.onCreated(function() {  
	this.subscribe('twitts');
});

Template.twiitPage.helpers({  
  'tweetMessage': function() {
    var tweet =  Twitts.findOne({_id: this.params._id}, { 
        sort: {timestamp: -1}
    });
    return tweet;
  }
});

Template.twiitPage.helpers({  
  'convertDateTime': function() {
  	var dateNew = new Date(this.timestamp);
  	var dateCon = dateNew.toLocaleString();
    return dateCon;
  }
});
