Template.notifications.helpers({
  notifications: function() {
    this.followins = UserUtils.findFollowings(Meteor.user().username);
    console.log(followins);
    return Notifications.find({user: { $in: followins }, read: false});
  },
  notificationCount: function(){
    var followins = UserUtils.findFollowings(Meteor.user().username);
    return Notifications.find({user: { $in: followins }, read: false}).count();
  }
});

Template.notificationItem.helpers({
  notificationTwiitPath: function() {
    var path = Router.routes.postPage.path({_id: this.twiitId});
    console.log(path);
    //return Router.routes.postPage.path({_id: this.twiitId});
  }
});

Template.notificationItem.events({
  'click a': function() {
    Notifications.update(this._id, {$set: {read: true}});
  }
});
