Router.configure({
	waitOn: function(){
		return Meteor.subscribe('notifications');
	}
});

Router.route('/', {name: 'userManagement'});
Router.route('/whoToFollow', {name: 'followUsers'});
Router.route('/myProfile', {name: 'userProfile'});
Router.route('/twiits/:_id', {
  name: 'twiitPage',
  data: function() { 
  	return this.params;
  }
});
