Template.tweetFeed.onCreated(function() {  
	console.log(Meteor.user());
	this.subscribe('twitts');
	if(Meteor.user()){
		this.subscribe('ownTwitts', Meteor.user().username);
	}
});

Template.tweetFeed.helpers({  
  'tweetMessage': function() {
    return Twitts.find({}, { 
        sort: {timestamp: -1}, 
        limit: 10
    });
  }
});
